import React, { useContext, useState, useEffect } from "react"
import axios from 'axios'
import { useAuth } from './authContext'

const BASE_URL = "http://localhost:5000/api/v1/"

axios.defaults.withCredentials = true  // Cookie'leri göndermek için

export const GlobalContext = React.createContext()

export const GlobalProvider = ({children}) => {
    const { user } = useAuth()
    const [incomes, setIncomes] = useState([])
    const [expenses, setExpenses] = useState([])
    const [error, setError] = useState(null)
    const [categories, setCategories] = useState([])
    const [darkMode, setDarkMode] = useState(false)

    const getIncomes = async () => {
        try {
            const response = await axios.get(`${BASE_URL}transactions/get-incomes/${user._id}`)
            setIncomes(response.data)
        } catch (error) {
            setError(error.response?.data?.message)
        }
    }

    const getExpenses = async () => {
        try {
            const response = await axios.get(`${BASE_URL}transactions/get-expenses/${user._id}`)
            setExpenses(response.data)
        } catch (error) {
            setError(error.response?.data?.message)
        }
    }

    const addIncome = async (income) => {
        if (!user) return; // Kullanıcı yoksa işlem yapma
        try {
            const response = await axios.post(`${BASE_URL}add-income`, {
                ...income,
                user: user._id
            })
            await getIncomes()
            setError('')
            return response.data
        } catch (err) {
            setError(err.response?.data?.message || 'Gelir eklenirken bir hata oluştu')
            throw err
        }
    }

    const addExpense = async (expense) => {
        if (!user) return; // Kullanıcı yoksa işlem yapma
        try {
            const response = await axios.post(`${BASE_URL}add-expense`, {
                ...expense,
                user: user._id
            })
            await getExpenses()
            setError('')
            return response.data
        } catch (err) {
            setError(err.response?.data?.message || 'Gider eklenirken bir hata oluştu')
            throw err
        }
    }

    const deleteIncome = async (id) => {
        if (!user) return;
        try {
            const response = await axios.delete(`${BASE_URL}delete-income/${id}`)
            await getIncomes()
            setError('')
            return response.data
        } catch (err) {
            setError(err.response?.data?.message || 'Gelir silinirken bir hata oluştu')
            throw err
        }
    }

    const deleteExpense = async (id) => {
        if (!user) return;
        try {
            const response = await axios.delete(`${BASE_URL}delete-expense/${id}`)
            await getExpenses()
            setError('')
            return response.data
        } catch (err) {
            setError(err.response?.data?.message || 'Gider silinirken bir hata oluştu')
            throw err
        }
    }

    const totalIncome = () => {
        let totalIncome = 0;
        incomes.forEach((income) =>{
            totalIncome = totalIncome + income.amount
        })
        return totalIncome;
    }

    const totalExpenses = () => {
        let totalExpense = 0;
        expenses.forEach((expense) =>{
            totalExpense = totalExpense + expense.amount
        })
        return totalExpense;
    }

    const totalBalance = () => {
        return totalIncome() - totalExpenses()
    }

    const transactionHistory = () => {
        const history = [...incomes, ...expenses]
        history.sort((a, b) => {
            return new Date(b.date) - new Date(a.date)
        })
        return history.slice(0, 3)
    }

    const addCategory = async (category) => {
        try {
            const response = await axios.post(`${BASE_URL}categories/add-category`, category)
            getCategories(category.type)
        } catch (error) {
            setError(error.response.data.message)
        }
    }

    const getCategories = async (type) => {
        try {
            const response = await axios.get(`${BASE_URL}categories/get-categories${type ? `?type=${type}` : ''}`)
            setCategories(response.data)
        } catch (error) {
            setError(error.response?.data?.message)
        }
    }

    const deleteCategory = async (id) => {
        try {
            const response = await axios.delete(`${BASE_URL}categories/delete-category/${id}`)
            getCategories()
        } catch (error) {
            setError(error.response.data.message)
        }
    }

    const toggleTheme = () => {
        setDarkMode(!darkMode)
    }

    useEffect(() => {
        if (user?._id) {
            getIncomes()
            getExpenses()
            getCategories()
        }
    }, [user])

    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            totalIncome,
            addExpense,
            getExpenses,
            expenses,
            deleteExpense,
            totalExpenses,
            totalBalance,
            transactionHistory,
            error,
            setError,
            categories,
            addCategory,
            getCategories,
            deleteCategory,
            darkMode,
            toggleTheme
        }}>
            {children}
        </GlobalContext.Provider>
    )
}

export const useGlobalContext = () => {
    return useContext(GlobalContext)
}
