import React, { useEffect } from 'react'
import styled from 'styled-components'
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/authContext';
import { signout } from '../../utils/Icons'

function Navigation() {
    const navigate = useNavigate()
    const location = useLocation()
    const { logout } = useAuth()

    const handleLogout = async () => {
        await logout()
        navigate('/login')
    }

    const handleNavigation = (path) => {
        navigate(path)
    }

    return (
        <NavStyled>
            <div className="user-con">
                <div className="text">
                    <h2>Bütçe Takip</h2>
                </div>
            </div>
            <ul className="menu-items">
                <li 
                    className={`${location.pathname === '/' || location.pathname === '/dashboard' ? 'active' : ''}`}
                    onClick={() => handleNavigation('/dashboard')}
                >
                    <span>Dashboard</span>
                </li>
                <li 
                    className={`${location.pathname === '/incomes' ? 'active' : ''}`}
                    onClick={() => handleNavigation('/incomes')}
                >
                    <span>Gelirler</span>
                </li>
                <li 
                    className={`${location.pathname === '/expenses' ? 'active' : ''}`}
                    onClick={() => handleNavigation('/expenses')}
                >
                    <span>Giderler</span>
                </li>
                <li onClick={handleLogout}>
                    <span>{signout} Çıkış Yap</span>
                </li>
            </ul>
        </NavStyled>
    )
}

const NavStyled = styled.nav`
    padding: 2rem 1.5rem;
    width: 374px;
    height: 100%;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;

    .user-con{
        height: 100px;
        display: flex;
        align-items: center;
        gap: 1rem;
        h2{
            color: rgba(34, 34, 96, 1);
        }
        p{
            color: rgba(34, 34, 96, .6);
        }
    }

    .menu-items{
        flex: 1;
        display: flex;
        flex-direction: column;
        li{
            display: grid;
            grid-template-columns: 40px auto;
            align-items: center;
            margin: .6rem 0;
            font-weight: 500;
            cursor: pointer;
            transition: all .4s ease-in-out;
            color: rgba(34, 34, 96, .6);
            padding-left: 1rem;
            position: relative;

            &.active{
                color: rgba(34, 34, 96, 1) !important;
                &::before{
                    content: "";
                    position: absolute;
                    left: 0;
                    top: 0;
                    width: 4px;
                    height: 100%;
                    background: #222260;
                    border-radius: 0 10px 10px 0;
                }
            }

            &:hover{
                color: rgba(34, 34, 96, 1);
            }
        }
    }
`;

export default Navigation;
