import React, { useContext, useState, useCallback } from "react"
import axios from 'axios'

const GlobalContext = React.createContext()

export const GlobalProvider = ({children}) => {
    const [incomes, setIncomes] = useState([])
    const [expenses, setExpenses] = useState([])
    const [error, setError] = useState(null)

    //calculate incomes
    const addIncome = async (income) => {
        try {
            await axios.post('http://localhost:5000/api/v1/add-income', income)
            getIncomes()
        } catch (err) {
            setError(err.response.data.message)
        }
    }

    const getIncomes = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/v1/get-incomes')
            setIncomes(response.data)
        } catch (err) {
            setError(err.response?.data?.message || 'Bir hata oluştu')
        }
    }, [])

    const deleteIncome = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/v1/delete-income/${id}`)
            getIncomes()
        } catch (err) {
            setError(err.response.data.message)
        }
    }

    const totalIncome = () => {
        let totalIncome = 0;
        incomes.forEach((income) =>{
            totalIncome = totalIncome + income.amount
        })

        return totalIncome;
    }

    //calculate expenses
    const addExpense = async (expense) => {
        try {
            await axios.post('http://localhost:5000/api/v1/add-expense', expense)
            getExpenses()
        } catch (err) {
            setError(err.response.data.message)
        }
    }

    const getExpenses = useCallback(async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/v1/get-expenses')
            setExpenses(response.data)
        } catch (err) {
            setError(err.response?.data?.message || 'Bir hata oluştu')
        }
    }, [])

    const deleteExpense = async (id) => {
        try {
            await axios.delete(`http://localhost:5000/api/v1/delete-expense/${id}`)
            getExpenses()
        } catch (err) {
            setError(err.response.data.message)
        }
    }

    const totalExpenses = () => {
        let totalExpense = 0;
        expenses.forEach((expense) =>{
            totalExpense = totalExpense + expense.amount
        })

        return totalExpense;
    }

    const totalBalance = () => {
        return totalIncome() - totalExpenses()
    }

    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            totalIncome,
            addExpense,
            getExpenses,
            expenses,
            deleteExpense,
            totalExpenses,
            totalBalance,
            error,
            setError
        }}>
            {children}
        </GlobalContext.Provider>
    )
}

export const useGlobalContext = () => {
    return useContext(GlobalContext)
}
