import React from 'react'
import styled from 'styled-components'
import { dateFormat } from '../../utils/dateFormat'
import { trash } from '../../utils/Icons'
import Button from '../Button/Button'

function CategoryItem({
    id,
    title,
    type,
    color,
    isBase,
    createdAt,
    deleteItem
}) {
    const handleDelete = async () => {
        try {
            await deleteCategory(id);
        } catch (error) {
            console.error('Kategori silme hatası:', error);
        }
    };

    return (
        <CategoryItemStyled color={color}>
            <div className="icon">
                <div className="color-box"></div>
            </div>
            <div className="content">
                <h5>{title}</h5>
                <div className="inner-content">
                    <div className="text">
                        <p>{dateFormat(createdAt)}</p>
                        {isBase && <span className="badge">Varsayılan</span>}
                    </div>
                    {!isBase && (
                        <div className="btn-con">
                            <Button 
                                icon={trash}
                                bPad={'0.4rem'}
                                bRad={'50%'}
                                bg={'rgba(255, 99, 99, 0.1)'}
                                color={'#FF6363'}
                                iColor={'#FF6363'}
                                hColor={'var(--color-green)'}
                                onClick={handleDelete}
                            />
                        </div>
                    )}
                </div>
            </div>
        </CategoryItemStyled>
    )
}

const CategoryItemStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    border-radius: 20px;
    padding: 1rem;
    display: flex;
    align-items: center;
    gap: 1rem;
    
    .icon {
        width: 40px;
        height: 40px;
        border-radius: 20px;
        background: #F5F5F5;
        display: flex;
        align-items: center;
        justify-content: center;
        
        .color-box {
            width: 20px;
            height: 20px;
            border-radius: 5px;
            background: ${props => props.color};
        }
    }

    .content {
        flex: 1;
        display: flex;
        flex-direction: column;
        gap: .2rem;
        
        h5 {
            font-size: 1.1rem;
            padding-left: 2rem;
            position: relative;
            &::before {
                content: '';
                position: absolute;
                left: 0;
                top: 50%;
                transform: translateY(-50%);
                width: 0.8rem;
                height: 0.8rem;
                border-radius: 50%;
                background: ${props => props.color};
            }
        }

        .inner-content {
            display: flex;
            justify-content: space-between;
            align-items: center;
            
            .text {
                display: flex;
                align-items: center;
                gap: 1.5rem;
                
                p {
                    display: flex;
                    align-items: center;
                    gap: 0.5rem;
                    color: var(--primary-color);
                    opacity: 0.8;
                }

                .badge {
                    background: rgba(34, 34, 96, 0.1);
                    padding: 0.2rem 0.6rem;
                    border-radius: 30px;
                    font-size: 0.8rem;
                    color: var(--primary-color);
                }
            }
        }
    }
`;

export default CategoryItem
