import React from 'react'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext'
import History from '../../History/History'
import { InnerLayout } from '../../styles/Layouts'
import { dollar } from '../../utils/Icons'
import Chart from '../Chart/Chart'

function Dashboard() {
    const {
        totalExpenses,
        totalIncome,
        totalBalance,
        getIncomes,
        getExpenses,
        incomes,
        expenses
    } = useGlobalContext()

    const minIncome = Math.min(...incomes.map(item => item.amount))
    const maxIncome = Math.max(...incomes.map(item => item.amount))
    const minExpense = Math.min(...expenses.map(item => item.amount))
    const maxExpense = Math.max(...expenses.map(item => item.amount))

    return (
        <DashboardStyled>
            <InnerLayout>
                <h1>Tüm İşlemler</h1>
                <div className="stats-con">
                    <div className="chart-con">
                        <Chart />
                        <div className="amount-con">
                            <div className="income">
                                <h2>Toplam Gelir</h2>
                                <p>
                                    {totalIncome() || 0} ₺
                                </p>
                            </div>
                            <div className="expense">
                                <h2>Toplam Gider</h2>
                                <p>
                                    {totalExpenses() || 0} ₺
                                </p>
                            </div>
                            <div className="balance">
                                <h2>Toplam Bakiye</h2>
                                <p>
                                    {totalBalance() || 0} ₺
                                </p>
                            </div>
                        </div>
                    </div>
                    <div className="history-con">
                        <History title="Son İşlemler" />
                        <div className="salary-title">
                            <h2>Min <span>Gelir</span>: {minIncome || 0}₺</h2>
                            <h2>Max <span>Gelir</span>: {maxIncome || 0}₺</h2>
                        </div>
                        <div className="salary-title">
                            <h2>Min <span>Gider</span>: {minExpense || 0}₺</h2>
                            <h2>Max <span>Gider</span>: {maxExpense || 0}₺</h2>
                        </div>
                    </div>
                </div>
            </InnerLayout>
        </DashboardStyled>
    )
}

const DashboardStyled = styled.div`
    .stats-con {
        display: grid;
        grid-template-columns: repeat(5, 1fr);
        gap: 2rem;
        .chart-con {
            grid-column: 1 / 4;
            height: 400px;
            .amount-con {
                display: grid;
                grid-template-columns: repeat(4, 1fr);
                gap: 2rem;
                margin-top: 2rem;
                .income, .expense {
                    grid-column: span 2;
                }
                .income, .expense, .balance {
                    background: #FCF6F9;
                    border: 2px solid #FFFFFF;
                    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
                    border-radius: 20px;
                    padding: 1rem;
                    p {
                        font-size: 2.5rem;
                        font-weight: 700;
                    }
                }
                .balance {
                    grid-column: 2 / 4;
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                    p {
                        color: var(--color-green);
                        opacity: 0.6;
                        font-size: 2.5rem;
                    }
                }
            }
        }

        .history-con {
            grid-column: 4 / -1;
            h2 {
                margin: 1rem 0;
                display: flex;
                align-items: center;
                justify-content: space-between;
            }
            .salary-title {
                margin: 1rem 0;
                h2 {
                    font-size: 1.2rem;
                    span {
                        font-size: 1.2rem;
                        color: var(--color-green);
                    }
                }
            }
        }
    }
`;

export default Dashboard
