import React, { useState } from 'react'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext'
import Button from '../Button/Button'
import { plus } from '../../utils/Icons'
import { CATEGORY_ICONS } from '../../utils/categoryIcons'

function CategoryForm({ type }) {
    const { addCategory, error, setError } = useGlobalContext()
    const [inputState, setInputState] = useState({
        title: '',
        type: type,
        color: '#000000',
        icon: 'circle'
    })

    const { title, color, icon } = inputState;

    const handleInput = name => e => {
        setInputState({...inputState, [name]: e.target.value})
        setError('')
    }

    const handleSubmit = e => {
        e.preventDefault()
        addCategory(inputState)
        setInputState({
            ...inputState,
            title: '',
            color: '#000000',
            icon: 'circle'
        })
    }

    return (
        <FormStyled onSubmit={handleSubmit}>
            {error && <p className='error'>{error}</p>}
            <div className="input-control">
                <input 
                    type="text" 
                    value={title}
                    name={'title'} 
                    placeholder={`${type === 'income' ? 'Gelir' : 'Gider'} Kategori Adı`}
                    onChange={handleInput('title')}
                />
            </div>
            <div className="input-control">
                <label>Kategori Rengi:</label>
                <input 
                    type="color" 
                    value={color}
                    onChange={handleInput('color')}
                />
            </div>
            <div className="selects input-control">
                <label>Kategori İkonu:</label>
                <select value={icon} onChange={handleInput('icon')}>
                    {Object.entries(CATEGORY_ICONS).map(([key, {icon, label}]) => (
                        <option key={key} value={key}>
                            {label}
                        </option>
                    ))}
                </select>
            </div>
            <div className="icon-preview">
                <label>Seçilen İkon:</label>
                <div className="icon-container" style={{background: color}}>
                    {CATEGORY_ICONS[icon]?.icon}
                </div>
            </div>
            <div className="submit-btn">
                <Button 
                    name={`${type === 'income' ? 'Gelir' : 'Gider'} Kategorisi Ekle`}
                    icon={plus}
                    bPad={'.8rem 1.6rem'}
                    bRad={'30px'}
                    bg={'var(--color-accent'}
                    color={'#fff'}
                />
            </div>
        </FormStyled>
    )
}

const FormStyled = styled.form`
    display: flex;
    flex-direction: column;
    gap: 2rem;

    input, select {
        font-family: inherit;
        font-size: inherit;
        outline: none;
        border: none;
        padding: .5rem 1rem;
        border-radius: 5px;
        border: 2px solid #fff;
        background: transparent;
        resize: none;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        color: rgba(34, 34, 96, 0.9);
        &::placeholder {
            color: rgba(34, 34, 96, 0.4);
        }
    }

    .input-control {
        display: flex;
        flex-direction: column;
        gap: .5rem;

        label {
            font-size: 0.9rem;
            color: var(--primary-color);
            opacity: 0.8;
        }

        input[type="color"] {
            padding: 0;
            width: 100px;
            height: 40px;
            cursor: pointer;
        }
    }

    .icon-preview {
        display: flex;
        flex-direction: column;
        gap: .5rem;

        label {
            font-size: 0.9rem;
            color: var(--primary-color);
            opacity: 0.8;
        }

        .icon-container {
            width: 40px;
            height: 40px;
            border-radius: 10px;
            display: flex;
            align-items: center;
            justify-content: center;
            border: 2px solid #FFFFFF;
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);

            i {
                font-size: 1.5rem;
                color: #fff;
            }
        }
    }

    .submit-btn {
        button {
            box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
            &:hover {
                background: var(--color-green) !important;
            }
        }
    }
`;

export default CategoryForm
